import React from "react"
import map from '../resources/map.jpeg'
export default function App() {
  
  const [latitude, setLatitude] = React.useState('');
  const [longitude, setLongitude] = React.useState('');

  React.useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  
  function convertCoordinatesSystem1ToSystem2(x1, y1) {
    // Coordinates in system 1
    var a = { x: 13.007769, y: 77.531260 };
    var b = { x: 13.008020, y: 77.650565 };
    var c = { x: 12.947968, y: 77.529458 };
    var d = { x: 12.949203, y: 77.651178 };

    // Coordinates in system 2
    var e = { x: 0, y: 0 };
    var f = { x: 5379, y: 0 };
    var g = { x: 0, y: 2788 };
    var h = { x: 5379, y: 2788 };

    // Calculate transformation parameters
    var mx = (f.x - e.x) / (b.x - a.x);
    var bx = e.x - mx * a.x;
    var my = (g.y - e.y) / (c.y - a.y);
    var by = e.y - my * a.y;

    // Apply transformation
    var x2 = mx * x1 + bx;
    var y2 = my * y1 + by;

    // Return the transformed coordinates
    return { x: x2, y: y2 };
}

// Example usage
var coordinatesSystem1 = { x: 77.621872, y: 12.987814 };
var coordinatesSystem2 = convertCoordinatesSystem1ToSystem2(coordinatesSystem1.x, coordinatesSystem1.y);

console.log("Coordinates in System 1:", coordinatesSystem1);
console.log("Coordinates in System 2:", coordinatesSystem2);


  return (
    <div style={{ overflow: 'auto', width: '100%', height: '100vh' }}>
      <img
        src={map}
        alt="Map"
        useMap="#workmap"
        width="5379"
        height="2788"
        style={{ width: '100%', height: 'auto' }}
      />
      <map name="workmap"></map>
    </div>
  );
  }
