import React from "react"
import map from '../resources/map.jpeg'
export default function App() {
  
  const [latitude, setLatitude] = React.useState('');
  const [longitude, setLongitude] = React.useState('');

  React.useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  
// Function to convert coordinates from actual map to your map
function convertCoordinates(actualX, actualY) {
  // Transformation parameters
  const a = 0.0001;  // Adjust this based on your mapping scale
  const b = 0;       // No shearing for simplicity
  const c = 0;       // No shearing for simplicity
  const d = 0.0001;  // Adjust this based on your mapping scale
  const e = 4105;    // Translation offset for X
  const f = 961;     // Translation offset for Y

  // Apply the transformation formula
  const newX = a * actualX + b * actualY + e;
  const newY = c * actualX + d * actualY + f;

  return { newX, newY };
}

// Example usage
const actualCoordinates = { x: 13.001782 , y: 77.585019 };
const convertedCoordinates = convertCoordinates(actualCoordinates.x, actualCoordinates.y);

console.log("Actual Coordinates:", actualCoordinates);
console.log("Converted Coordinates:", convertedCoordinates);


  return (
    <div style={{ overflow: 'auto', width: '100%', height: '100vh' }}>
      <img
        src={map}
        alt="Map"
        useMap="#workmap"
        width="5379"
        height="2788"
        style={{ width: '100%', height: 'auto' }}
      />
      <map name="workmap"></map>
    </div>
  );
  }
